-- TABLES
-- CLIENTE
CREATE TABLE Cliente
(
	Id_Cliente int PRIMARY KEY,
	Nombre NVARCHAR(50) NOT NULL,
	Apellido NVARCHAR(50) NOT NULL,
	Edad INT NOT NULL,
	Fecha_Nacimiento DATE NOT NULL,
	Sexo NVARCHAR(15) NOT NULL,
)

-- DIRECCION
CREATE TABLE Direccion
(
	Id_Direccion INT PRIMARY KEY,
	Id_Cliente INT,
	Nombre_Direccion NVARCHAR(150),
	Direccion NVARCHAR(150),
	Pais NVARCHAR(15),
	Provincia NVARCHAR(100),
	Ciudad NVARCHAR(150),
	Codigo_Postal NVARCHAR(5),
	Telefono NVARCHAR(9),
	CONSTRAINT constraint_id_cliente_direccion_fk FOREIGN KEY (Id_Cliente) REFERENCES Cliente (Id_Cliente)
)

-- USUARIO
CREATE TABLE Usuario
(
	Id_Usuario INT PRIMARY KEY,
	Id_Cliente INT NOT NULL,
	Usuario_Name NVARCHAR(50) NOT NULL,
	Contrasenia NVARCHAR(50) NOT NULL,
	Correo_Electronico NVARCHAR(50) NOT NULL,
	CONSTRAINT constraint_cliente_fk FOREIGN KEY (Id_Cliente) REFERENCES Cliente (Id_Cliente) ON DELETE CASCADE
)

-- TARJETA DE CREDITO
CREATE TABLE Tarjeta_Credito
(
	Id_Usuario INT,
	Titular NVARCHAR(100),
	Numero_Tarjeta NVARCHAR(16),
	Mes_Vencimiento NVARCHAR(4),
	Anio_Vencimiento NVARCHAR(4),
	CVV NVARCHAR(3),
	CONSTRAINT constraint_id_usuario_tarjeta_pk PRIMARY KEY (Id_Usuario),
	CONSTRAINT constraint_id_usuario_tarjeta_fk FOREIGN KEY (Id_Usuario) REFERENCES Usuario (Id_Usuario) ON DELETE CASCADE
)

-- DETALLE USUARIO
CREATE TABLE Detalle_Usuario
(
	Id_Usuario INT NOT NULL PRIMARY KEY,
	Fecha_Alta DATETIME NOT NULL,
	Fecha_Ultima_Modificacion DATETIME,
	CONSTRAINT constraint_id_usuario_fk FOREIGN KEY (Id_Usuario) REFERENCES Usuario (Id_Usuario) ON DELETE CASCADE
)
-- USUARIO INVITADO

CREATE TABLE Usuario_Invitado (
    Id_Usuario INT NOT NULL PRIMARY KEY,
    Fecha_Expiracion DATETIME,
    CONSTRAINT constraint_id_usuario_invitado_fk FOREIGN KEY (Id_Usuario) REFERENCES Usuario(Id_Usuario) ON DELETE CASCADE
)

-- CATEGORIA
CREATE TABLE Categoria
(
	Id_Categoria INT IDENTITY PRIMARY KEY,
	Descripcion NVARCHAR(50) NOT NULL
)

-- SUBCATEGORIA
CREATE TABLE Subcategoria
(
	Id_Subcategoria INT IDENTITY PRIMARY KEY,
	Id_Categoria INT NOT NULL,
	Descripcion NVARCHAR(50) NOT NULL,
	CONSTRAINT constraint_id_categoria_fk2 FOREIGN KEY (Id_Categoria) REFERENCES Categoria (Id_Categoria)
)

-- PRODUCTO
CREATE TABLE Producto
(
	Id_Producto INT IDENTITY PRIMARY KEY,
	Id_Subcategoria INT NOT NULL,
	Nombre NVARCHAR(50) NOT NULL,
	Descripcion NVARCHAR(250) NOT NULL,
	Precio DECIMAL(5,2) NOT NULL,
	Fabricante NVARCHAR(50) NOT NULL,
	CONSTRAINT constraint_id_subcategoria_fk FOREIGN KEY (Id_Subcategoria) REFERENCES Subcategoria (Id_Subcategoria)
)

-- PRODUCTO_IMAGEN
Use EasyShop
CREATE TABLE Imagen
(
	Id_Producto INT,
	Codigo_Imagen NVARCHAR(30)
	CONSTRAINT constraint_id_producto_pk PRIMARY KEY (Id_Producto),
	CONSTRAINT constraint_id_producto_img_fk FOREIGN KEY (Id_Producto) REFERENCES Producto (Id_Producto) ON DELETE CASCADE
)

-- ESTADO
CREATE TABLE Estado_Pedido
(
	Id_Estado_Pedido INT IDENTITY PRIMARY KEY,
	Descripcion NVARCHAR(50)
)

-- PEDIDO
CREATE TABLE Pedido
(
	Id_Pedido INT PRIMARY KEY,
	Id_Cliente INT NOT NULL,
	Id_Estado_Pedido int NOT NULL,
	Total DECIMAL(5,2) NOT NULL,
	Fecha DATETIME NOT NULL,
	CONSTRAINT constraint_id_cliente_pedido_fk FOREIGN KEY (Id_Cliente) REFERENCES cliente (Id_Cliente),
	CONSTRAINT constraint_id_estado_pedido FOREIGN KEY (Id_Estado_Pedido) REFERENCES estado_pedido (Id_Estado_Pedido)
)

-- LINEA_PEDIDO
CREATE TABLE Linea_Pedido
(
	Id_Linea_Pedido INT PRIMARY KEY,
	Id_Pedido INT NOT NULL,
	Id_Producto INT NOT NULL,
	Cantidad INT NOT NULL,
	Subtotal DECIMAL(5,2) NOT NULL,
	IVA DECIMAL(5,2) NOT NULL,
	CONSTRAINT constraint_id_pedido_fk FOREIGN KEY (Id_Pedido) REFERENCES Pedido (Id_Pedido) ON DELETE CASCADE,
	CONSTRAINT constraint_id_producto_fk FOREIGN KEY (Id_Producto) REFERENCES producto (Id_Producto)
)

-- FACTURA
CREATE TABLE Factura (
    Id_Factura INT PRIMARY KEY,
    Id_Pedido INT NOT NULL,
    Fecha_Factura DATETIME,
    CONSTRAINT constraint_id_pedido_factura_fk FOREIGN KEY (Id_Pedido) REFERENCES pedido (Id_Pedido) ON DELETE CASCADE
)

-- CARRITO
CREATE TABLE Carrito
(
	Id_Usuario INT,
	Id_Producto INT,
	Cantidad INT NOT NULL
	CONSTRAINT constraint_carrito_pk PRIMARY KEY (Id_Usuario, Id_Producto),
	CONSTRAINT constraint_usuario_fk FOREIGN KEY (Id_Usuario) REFERENCES Usuario (Id_Usuario) ON DELETE CASCADE,
	CONSTRAINT constraint_producto_fk FOREIGN KEY (Id_Producto) REFERENCES Producto (Id_Producto)
)

